
public class PrimitiveFloatDataTypeDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int value1=9/2;
		float value2=10/6f;
		double value3=10/6d;
		System.out.println("Value 1 ="+value1+"Value 2 ="+value2+"Value 3 ="+value3);
		int marker=512;
		double percentage=marker*0.46f;
		System.out.println("Percentage ="+percentage);
	}

}
